</template>

<script>

import * as panzoom from 'panzoom';

export default {
  name: 'Map',
  props: {
    offset: Number,
    contentActive: Boolean
  },
  data: function () {
    return {
      panzoomInstance: null,
    }
  },
  watch: {
    // whenever offset changes, this function will run
    offset: function (oldOffset, newOffset) {
        if (newOffset === 0) {
            this.moveBy(0, oldOffset);
        } else {
            this.moveBy(0, -newOffset/2);
        }
    },
    contentActive: function (oldBoolean, newBoolean) {
        if (newBoolean === true) {
            this.moveToInactive();
        } else {
            this.moveToActiveAlt();
        }
    }
  },
  mounted() {
    this.panzoomInstance = panzoom(this.$refs.panmap, {
        maxZoom: 2,
        minZoom: 0.25
        });
    this.moveToActive();

  },
    methods: {
    controlsClick() {
        console.log('controls');
    },
    moveBy(x, y) {
        this.panzoomInstance.moveBy(x, y);
    },
    moveTo(x, y) {
        this.panzoomInstance.moveTo(x, y);
    },
    smoothZoom(x, y, scale) {
        this.panzoomInstance.smoothZoom(x, y, scale);
    },
    moveToActive() {
        console.log('move to active');
        this.moveTo(-1000 + (window.innerWidth/2), -1000 + ((198 + 48)/2));
    },
    moveToActiveAlt() {
        console.log('move to active');
        this.moveTo(-1000 + (window.innerWidth/2), -1000 + ((198 + 48)/2) + this.offset);
    },
    moveToInactive() {
        console.log('move to inactive');
        this.moveTo(-1000 + (window.innerWidth/2), -1000 + ((198 + 48 + this.offset)/2));
    }
   },
}

</script>

<style>

  .map {
    background-color: #d7d7d7;
    position: absolute;
    overflow: hidden;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
  }

  .map-tiles-inner {
    position: relative;
    width: 2000px;
    height: 2000px;
  }

  .map-viewport {
    position: fixed;
    top: 48px;
    left: 0;
    right: 0;
  }

  .marker {
    position: absolute;
    width: 15px;
    height: 30px;
    background-color: #9b9b9b;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
  }

  .controls {
    width: 30px;
    height: 60px;
    background-color: #9b9b9b;
    position: absolute;
    top: 20px;
    right: 20px;
  }


</style>